package main

import (
    "encoding/json"
    "fmt"
)

type Animal struct {
    Name  string
    Order string
}

func main() {
    var jsonBlob = []byte(`[
        {"Name": "Platypus", "Order": "Monotremata"},
        {"Name": "Quoll",    "Order": "Dasyuromorphia"}
    ]`)
    var animals []Animal
