使用 tags
package main

import (
    "encoding/json"
    "fmt"
)

type User struct {
    Name string   `json:"name"`
    Age  int      `json:"age,omitempty"`
    Tags []string `json:"tags"`
}

func main() {
    user := &User{
        Name: "bob",
        Tags: []string{"sport", "music"},
    }
    b, _ := json.Marshal(user)
    fmt.Println(string(b))
    // {"name":"bob","tags":["sport","music"]}

    newUser := User{}
    json.Unmarshal(b, &newUser)
    fmt.Println(newUser)
